import React, { useState } from "react";

import "./Button.css";

const Button = (props) => {
  const [isInactive, setIsInactive] = useState(true);

  if(props.noInput){
    setIsInactive(true)
  }
  return (
    <button className={`button ${!isInactive?'inactive':''}`} type={props.type} onClick={props.onClick}>
      {props.children}
    </button>
  );
};

export default Button;
