import React,{useState}from "react";

import "./Button.css";

const Button = (props) => {
  const [isInactive, setIsInactive] = useState(true);
  if(!isInactive){
    setIsInactive(false);
    props.isInactive();
  }
  return (
    <button
      type={props.type}
      className={`button ${!isInactive ? "inactive" : ""}`}
      onClick={props.onClick}
    >
      {props.children}
    </button>
  );
};

export default Button;
